import React from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
// import axios from "axios";
import { useProductContext } from "../componetns/layout/home/componetns/layout/Products/productContext";
const LongProduct = () => {
  const { products } = useProductContext(); // Use the context hook
  const navigate = useNavigate();



  // Function to handle product click and navigate to SingleProduct page
  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="bg-gray-100">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="mx-auto max-w-2xl py-16 sm:py-24 lg:max-w-none lg:py-32">
          <h2 className="text-2xl font-bold text-gray-900">Our Product section</h2>

          <div className="mt-6 space-y-12 lg:grid lg:grid-cols-3 lg:gap-x-6 lg:space-y-0">
            {products.map((product) => (
              <div key={product.name} className="group relative" onClick={() => handleProductClick(product._id)}>
                <div className="relative h-80 w-full overflow-hidden rounded-lg bg-white sm:aspect-h-1 sm:aspect-w-2 lg:aspect-h-1 lg:aspect-w-1 group-hover:opacity-75 sm:h-64">
                  <img
                    src={product.images[0]} // Assuming images is an array
                    alt={product.name}
                    className="h-full w-full object-cover object-center"
                  />
                </div>
                <h3 className="mt-6 text-sm text-gray-500">
                  <Link to={product.href}>
                    <span className="absolute inset-0" />
                    {product.name}
                  </Link>
                </h3>
                <p className="text-base font-semibold text-gray-900">
                  {product.description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LongProduct;
